import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';

import { connect } from "react-redux";
import { fetchData, addMyList, removeMyList } from '../action';

const ShowList = props => {
    const [onHover, changeHover] = useState(null);
    const title = props.title.replace(" ","").toLowerCase();
    const clickHandler = () => {
        switch (title){
            case "mylist":
                return props.removeMyList;
            case "recommendations":
                return props.addMyList;
            default:
                return null;
        }
    }
    useEffect(()=>{
        props.fetchData(title);
    });

    const renderList = () => {
        if(Array.isArray(props.data) === true){
            return props.data.map(item => {
                return (
                    <div className="ui three wide column" 
                    key={item.id} 
                    onMouseOver={()=>changeHover(item.id)}
                    onMouseOut={()=>changeHover(null)}>
                        <img className="ui medium image" alt="" src={item.img} />
                        <div onMouseOut={(event)=> event.stopPropagation()}>{item.title}</div>
                        <div style={{height:30+'px'}} 
                        onMouseOut={(event)=> event.stopPropagation()}>
                            {onHover===item.id&&
                            <button 
                            className="tiny ui inverted primary fluid button " 
                            onClick={()=>clickHandler(item)}>
                                {props.buttonText}
                            </button>}
                        </div>
                    </div>
                )
            });
        }else{
            return <div>loading...</div>
        }                
    }

    return(
        <div>
            <h3 className="first">{props.title}</h3>
            <div className="ui grid">
                {renderList()}
            </div>                               
        </div>
    )
    
}

ShowList.propTypes = {
    // data: PropTypes.oneOfType([
    //     PropTypes.object,
    //     PropTypes.array
    // ]).isRequired,
    title: PropTypes.string.isRequired,
    // clickHandler: PropTypes.func.isRequired,
    buttonText: PropTypes.string.isRequired
}
const mapStateToProps = state => {
    console.log(state);
    console.log(ShowList.title)
    return {data:state[ShowList.title]}
}
export default connect(mapStateToProps, {fetchData,addMyList,removeMyList})(ShowList);
