import React, {useState, useEffect} from 'react';
import { connect } from 'react-redux';
import { fetchData, removeMyList } from '../action';
import RenderList from './RenderList';

const ShowList = props => {
    const [onHover, changeHover] = useState(null);
    useEffect(()=>{
        props.fetchData(props.title.replace(" ","").toLowerCase());
    },[...props.data]);


    return(
        <>
            <h3 className="first">{props.title}</h3>
            <div className="ui grid">
                <RenderList 
                data={props.data} 
                currentId={onHover} 
                buttonText="Remove"
                changeState={(value)=>changeHover(value)} 
                clickHandler={props.removeMyList} />
            </div>                               
        </>
    )
    
}
const mapStateToProps = (state) => {
    return {data:state.mylist}
}
export default connect(mapStateToProps,{fetchData,removeMyList})(ShowList);
